import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ExamsBox.css';
import FazerDownload from './DownloadBox';

function ExamsBox() {
    const navigate = useNavigate();
    const [comandoSair, setComandoSair] = useState('');
    var respostaDoBackend

    const logout = async () => {
        try {
            const resposta = await axios.post('http://localhost:4000/api/enviar-dados/logout', {
                mensagem: 'Quero sair',
            });
            setComandoSair(resposta.data);
            if (resposta.data.mensagem === 'Saia') {
                navigate('/login');
            }
        } catch (erro) {
            console.error('Erro ao enviar dados para o backend:', erro);
        }
    };

    useEffect(() => {
        lista();
    }, []);

    const lista = async () => {
        try {
            var resposta = await axios.post('http://localhost:4000/api/enviar-dados/user/exames/listar', {
                mensagem: 'Me dê os exames desse paciente',
            });
            // Aqui você pode fazer o que quiser com a resposta do backend
            console.log('Resposta do backend:', resposta.data);
            respostaDoBackend = resposta.data.mensagem
            console.log(respostaDoBackend)
        } catch (erro) {
            console.error('Erro ao enviar dados para o backend:', erro);
        }
        
    };

    const alternarEstado = () => {
        logout();
    };

    return (
      <div>
          <html lang="pt-br" dir="ltr">
              <head>
                  <meta charSet="utf-8" />
                  <title>Site dos Hospitais UPE</title>
                  <link rel="stylesheet" href="examestyles.css" />
                  <link rel="preconnect" href="https://fonts.googleapis.com" />
                  <link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin="true" />
                  <link
                      href="https://fonts.googleapis.com/css2?family=Poppins:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap"
                      rel="stylesheet"
                  />
              </head>
              <body>
                  <div className="background">
                      <div className="central">
                          <a href="#" className="logoleft">
                              <i className="logoupe"></i>
                          </a>
                          <ul className="lista">
                              <li>
                                  <a style={{ cursor: 'pointer' }} className="botaosair" onClick={alternarEstado}>
                                      Sair
                                  </a>
                              </li>
                          </ul>
                          <div className="clear"></div>
                          
                          {/* Conteúdo da lista de exames */}
                          <div className="exames-container">
                              <h2>Lista de Exames</h2>
                              <ul className="exames-list">
    {/* Se houver exames e respostaDoBackend estiver definido, renderiza cada um */}
    {respostaDoBackend && respostaDoBackend.length > 0 ? (
        respostaDoBackend.map((exame, index) => (
            <li key={index}>{exame}</li>
        ))
    ) : (
        /* Se não houver exames ou respostaDoBackend ainda não estiver definido, exibe uma mensagem */
        <li>Nenhum exame disponível</li>
    )}
</ul>
                          </div>
                      </div>
                  </div>
              </body>
          </html>
      </div>
  );
  
}

export default ExamsBox;
