import React, { useState } from 'react';
import axios from 'axios';
import './DownloadBox.css';
const fs = require ('fs')
const http = require('http')

function FazerDownload() {


function downloadFile(url, destinationPath) {
    const file = fs.createWriteStream(destinationPath);

    // Use http ou https conforme necessário
    http.get(url, response => {
        response.pipe(file);

        file.on('finish', () => {
            file.close(() => {
                console.log(`Arquivo baixado para: ${destinationPath}`);
            });
        });
    }).on('error', error => {
        fs.unlink(destinationPath, () => {}); // Exclua o arquivo, se existir, em caso de erro
        console.error('Erro ao baixar o arquivo:', error);
    });
}

// Exemplo de uso:
const url = 'Arquivos_Enviados_Upload'; // URL do arquivo a ser baixado
const destinationPath = 'C:\sistema-hospitais-UPE\backend\Arquivos_Baixados'; // Caminho de destino para salvar o arquivo

//downloadFile(url, destinationPath);

}

export default FazerDownload;
