import React, { useState } from 'react';
import axios from 'axios';
import logo from '../upe.jpg';
import './RegisterBox.css';
import { Link } from 'react-router-dom';



function RegisterBox() {
  var [respostaDoBackend, setRespostaDoBackend] = useState('');
  const [cpf, setCPF] = useState('');
  const [data, setData] = useState('');
  const [senha, setSenha] = useState('');
  const [email, setEmail] = useState('');
  const [login, setLogin] = useState('');
  const [nome, setnome] = useState('');

  const enviarDadosParaBackend = async (e) => {
    e.preventDefault(); 

    try {
      var resposta = await axios.post('http://localhost:4000/api/enviar-dados/registrar', {
        
        email: email,
        login: login,
        cpf: cpf,
        date: data,
        senha: senha,
        nome: nome,
      });
      setRespostaDoBackend(resposta.data);
    } catch (erro) {
      console.error('Erro ao enviar dados para o backend:', erro);
    }
  };
  
  
  
//   const RecarregarTela = async (e) => {
//     e.preventDefault(); 

//     try {
//       const resposta = await axios.post('http://localhost:4000/api/enviar-dados/registrar/recarregar','sim');
//     } catch (erro) {
//       console.error('Erro ao entrar na tela de login:', erro);
//     }
//   };
  
  const exibirMensagem = () => {
    if (respostaDoBackend.mensagem === 'Dados recebidos com sucesso!') {
      alert('Cadastro realizado com sucesso!');
      window.location.href = '/login'

    }
    

    // else {
    //   alert(respostaDoBackend.mensagem);
    // }
  };

  return (
    <div className="screen">
      <div className="register-card">
        <img src={logo} alt="logo" />
        <h1>Registro</h1>
        <form onSubmit={enviarDadosParaBackend}>
          

        <input
            type="email"
            name="email"
            placeholder="E-mail"
            onChange={(e) => setEmail(e.target.value)}
            value={email}
          />
            <input
            name="nome"
            placeholder="Nome completo"
            onChange={(e) => setSenha(e.target.value)}
            value={nome}
          />

          <input
            name="login"
            placeholder="Login"
            onChange={(e) => setLogin(e.target.value)}
            value={login}
          />
          
          <input
            name="cpf"
            placeholder="CPF"
            onChange={(e) => setCPF(e.target.value)}
            value={cpf}
          />
          <input
            type="date"
            name="data"
            placeholder="Data de Nascimento"
            onChange={(e) => setData(e.target.value)}
            value={data}
          />
          <input
            type="password"
            name="senha"
            placeholder="Senha"
            onChange={(e) => setSenha(e.target.value)}
            value={senha}
          />


          <button type="submit" className="register-btn" onClick={exibirMensagem}>
            Registrar
          </button>
        </form>
        {respostaDoBackend && (
          <p>{respostaDoBackend.mensagem}</p>
        )}
        
      </div>
    </div>
  );
}

export default RegisterBox;
