import React, { useState } from 'react';
import axios from 'axios';
import logo from '../upe.jpg';
import './RegisterBox.css';
import { Link } from 'react-router-dom';



function RegisterBox() {
  const [respostaDoBackend, setRespostaDoBackend] = useState('');
  const [cpf, setCPF] = useState('');
  const [data, setData] = useState('');
  const [senha, setSenha] = useState('');
  const [email, setEmail] = useState('');
  const [login, setLogin] = useState('');

  const enviarDadosParaBackend = async (e) => {
    e.preventDefault(); // Evita que o formul치rio recarregue a p치gina

    try {
      const resposta = await axios.post('http://localhost:4000/api/enviar-dados/registrar', {
        
        email: email,
        login: login,
        cpf: cpf,
        date: data,
        senha: senha,
      });
      setRespostaDoBackend(resposta.data);
    } catch (erro) {
      console.error('Erro ao enviar dados para o backend:', erro);
    }
  };
  
  const exibirMensagem = () => {
    if (respostaDoBackend.mensagem === 'Dados recebidos com sucesso!') {
      alert('Cadastro realizado com sucesso!');
      <Link to="/home">
    </Link>
    } else {
      alert('Ocorreu um erro ao cadastrar');
    }
  };

  return (
    <div className="screen">
      <div className="register-card">
        <img src={logo} alt="logo" />
        <h1>Registro</h1>
        <form onSubmit={enviarDadosParaBackend}>
          

        <input
            type="email"
            name="email"
            placeholder="E-mail"
            onChange={(e) => setEmail(e.target.value)}
            value={email}
          />

          <input
            name="login"
            placeholder="Login"
            onChange={(e) => setLogin(e.target.value)}
            value={login}
          />
          
          <input
            name="cpf"
            placeholder="CPF"
            onChange={(e) => setCPF(e.target.value)}
            value={cpf}
          />
          <input
            type="date"
            name="data"
            placeholder="Data de Nascimento"
            onChange={(e) => setData(e.target.value)}
            value={data}
          />
          <input
            type="password"
            name="senha"
            placeholder="Senha"
            onChange={(e) => setSenha(e.target.value)}
            value={senha}
          />
          <button type="submit" className="register-btn" onClick={exibirMensagem}>
            Registrar
          </button>
        </form>
        {/* {respostaDoBackend && (
          <p>Resposta do backend: {respostaDoBackend.mensagem}</p>
        )} */}
      </div>
    </div>
  );
}

export default RegisterBox;
