// LoginBox.js
import React from 'react';
import logo from "../logo.png";
import { Link } from 'react-router-dom';
import styles from './LoginBox.module.css'; // Importe os estilos do módulo CSS

// Componente da caixa de login
function LoginBox() {
  return (
    <div className={styles.screen}> {/* Use a classe do módulo CSS */}
      <div className={styles['login-card']}> {/* Use a classe do módulo CSS */}
        <img src={logo} alt="logo" />

        <h1>Login</h1>
        <input className={styles.input} name="username" placeholder="Nome" /> {/* Use a classe do módulo CSS */}
        <input className={styles.input} type="password" name="password" placeholder="Senha" /> {/* Use a classe do módulo CSS */}

        <button className={styles['login-btn']}>Entrar</button> {/* Use a classe do módulo CSS */}

        {/* Adicionando um botão para navegar para a página de registro */}
        <Link to="/register">
          <button className={styles['register-btn']}>Ir para o Registro</button> {/* Use a classe do módulo CSS */}
        </Link>
      </div>
    </div>
  );
}

export default LoginBox;
