import React from 'react';
import logo from '../upe.jpg';
import './RegisterBox.css'


function RegisterBox() {

    const [respostaDoBackend, setRespostaDoBackend] = useState('');
    const [mensagem, setMensagem] = useState('');
    
    const enviarDadosParaBackend = async () => {
        try {
          const resposta = await axios.post('http://localhost:4000/api/enviar-dados', {
            mensagem: mensagem
          });
          setRespostaDoBackend(resposta.data);
        } catch (erro) {
          console.error('Erro ao enviar dados para o backend:', erro);
        }
      };



  return (
    <div className="screen">
      <div className="register-card">
        <img src={logo} alt="logo" />

        <h1>Registro</h1>
        <form onSubmit={enviarDadosParaBackend}>
            <input name="username" placeholder="CPF" />
            <input type="password" name="password" placeholder="Senha" />

        <button type="submit" className="register-btn">Registrar</button>
        </form>
      </div>
    </div>
  );
}

export default RegisterBox;
